import styles from "./app.module.css";
import { data } from "../../utils/data";
import AppHeader from "../app-header/app-header"
import Modal from "../modal/modal";
import Overlay from "../overlay/overlay";
import Main from "../main/main";
import { useState } from "react";
import OrderDitails from "../modal/order-ditails/order-ditails";
import IngredientDitails from "../modal/ingredient-dilails/ingredient-dilails";

function App() {
  const root = document.getElementById('root');
  root.classList.add(styles.app);

  const [isModalIngOpen, setIsModalIngOpen] = useState(false);
  const [isModalConstructOpen, setIsModalConstructOpen] = useState(false);

  return (
    <div className={styles.page}>
      <AppHeader />
      <Main setIsModalIngOpen={setIsModalIngOpen} setIsModalConstructOpen={setIsModalConstructOpen} />
      {isModalIngOpen && (
        <>
          <Modal setIsModalIngOpen={setIsModalIngOpen} >
            <IngredientDitails />
          </Modal>
          <Overlay setIsModalIngOpen={setIsModalIngOpen} />
        </>
      )}
      {isModalConstructOpen && (
        <>
          <Modal setIsModalConstructOpen={setIsModalConstructOpen} >
            <OrderDitails />
          </Modal>
          <Overlay setIsModalConstructOpen={setIsModalConstructOpen} />
        </>
      )}
    </div>
  );
}

export default App;
