import React, { useEffect } from 'react'
import styles from './modal.module.css'
import done from '../../images/graphics.svg'
import ReactDOM from 'react-dom'
import iconClose from '../../images/icon 24x24.svg'

const modalRoot = document.getElementById('react_modal');

function Modal({ setIsModalOpen }) {

    useEffect(() => {
        document.addEventListener('ketdown', onEsc);

        function onEsc(e) {
            if (e.code==='Escape') {
                setIsModalOpen(false)
            }
        }

        return () => document.removeEventListener('keydown', onEsc);
    }, [])

    return ReactDOM.createPortal(
        (
            <div className={styles.modal}>
                <button className={['mt-15 mr-10', styles.button_close].join(' ')}><img src={iconClose} alt='закрыть' /></button>
                <p className={['text text_type_digits-large mt-30', styles.order_number].join(' ')}>034536</p>
                <p className='text text_type_main-medium  mt-8'>идентификатор заказа</p>
                <img className='mt-15' src={done} alt='галочка' />
                <p className='mt-15 text text_type_main-small'>Ваш заказ начали готовить</p>
                <p className='mt-2 text text_type_main-default text_color_inactive'>Дождитесь готовности на орбитальной станции</p>
            </div>
        ), modalRoot
    )
}

export default Modal
