import styles from "./app.module.css";
import { data } from "../../utils/data";
import AppHeader from "../app-header/app-header"
import Modal from "../modal/modal";
import Overlay from "../overlay/overlay";
import Main from "../main/main";
import { useState } from "react";
import OrderDitails from "../modal/order-ditails/order-ditails";

function App(props) {
  const root = document.getElementById('root');
  root.classList.add(styles.app);

  const [isModalOpen, setIsModalOpen] = useState(false);
  const [modalToggle, setModalToggle] = useState(<OrderDitails />)

  return (
    <div className={styles.page}>
      <AppHeader />
      <Main setIsModalOpen={setIsModalOpen} setModalToggle={setModalToggle} />
      {isModalOpen && modalToggle && (
        <>
          <Modal setIsModalOpen={setIsModalOpen} setModalToggle={setModalToggle} >
           {props.children}
          </Modal>
          <Overlay setIsModalOpen={setIsModalOpen} setModalToggle={setModalToggle} />
        </>
      )}
    </div>
  );
}

export default App;
